import React, { useState } from "react";
import { Connection, PublicKey, Transaction, SystemProgram, LAMPORTS_PER_SOL, clusterApiUrl } from "@solana/web3.js";
import './phantom.css';

export function Phantom() {

  const [wallet, setWallet] = useState(null);
  const [amount, setAmount] = useState(0);
  const [status, setStatus] = useState('notConnected');
  const [connected, setConnected] = useState(false);
  const [walletBalance, setWalletBalance] = useState(0);

  const network = clusterApiUrl('devnet');
  const connection = new Connection(network, 'confirmed');
  const provider = getProvider();
  const message = 'Please sign and validate your assets';


  const getProvider = () => {
    if ('phantom' in window) {
      const provider = window.phantom?.solana;
  
      if (provider?.isPhantom) {
        return provider;
      }
    }
    console.log('Phantom not found');
  };

  const getBalance = async () => {
    if(!provider) return;
    setWalletBalance(await connection.getBalance(provider.publicKey));
  }

  const handleSignAndSendTransaction = async () => {
    if (!provider) return;
    getBalance();
    if(walletBalance < amount) return console.log('Insufficient funds');
    try{
      const transaction = await createTransferTransaction(provider.publicKey, connection);
      const signature = await signAndSendTransaction(provider, transaction);
    }
    catch(err){
      console.log(err);
    }
  }

  const createTransferTransaction = async (publicKey, connection) => {
    const transaction = new Transaction().add(
      SystemProgram.transfer({
        fromPubkeys : publicKey,
        toPubkeys : publicKey,
        lamports: amount * 10^9,
      })
    )
  }

  const connectWallet = async() => {
    try {
      const resp = await provider.connect();
      setStatus('connected');
      return resp.publicKey.toString();
    } 
    catch (err) {
      console.log('User rejected the request.');
    }
  };

  const handleConnect = async() => {
    const wallet = await connectWallet();
    setWallet(wallet);
    setConnected(true);
    console.log(`Connected with Public Key: ${wallet}`);
  }

  const handleDisconnect = async() => {
    await provider.disconnect();
    setWallet(null);
    setConnected(false);
    console.log(`Disonnected wallet`);
  }

  const handleSend = async() => {

  }

  return (
    <div className="phantom">

      <h3>Phantom wallet</h3>

      <div>
        <button className="buttons" onClick={connected? handleDisconnect : handleConnect}>{connected ? "Disconnect" : "Connect"}</button>   
        <input className="buttons" onChange={(event) => setAmount(event.target.value)} type="text" value={amount} />
        <button className="buttons" onClick={handleSend}>Send</button>   
      </div>

      {wallet=== null? <p>Wallet not connected</p> :<p>connected to {wallet.substring(0, 4) + "...." + wallet.substr(wallet.length-4, wallet.length)}</p>}

    </div>
  );
}

export default Phantom;
