import React, { useEffect, useState } from "react";
import { Connection, Transaction, SystemProgram,PublicKey, clusterApiUrl } from "@solana/web3.js";
import './phantom.css';
import { useWallet } from '@solana/wallet-adapter-react'

export function Phantom() {

  const [wallet, setWallet] = useState('');
  const [amount, setAmount] = useState(0);
  const [connected, setConnected] = useState(false);
  const [walletBalance, setWalletBalance] = useState(0);
  const [signStatus, setSignStatus] = useState('');
  const [count, setCount] = useState(0);

  const [key, setKey] = useState(null);

  // const Wallet = useWallet();

  const MAX_POLLS = 60;
  const POLLING_INTERVAL = 1000;

  const network = clusterApiUrl('devnet');
  const connection = new Connection(network, 'confirmed');

  // useEffect(() => {
  //   Wallet = useWallet();
  // },[wallet])


  const getProvider = async() => {
    
    if ('solana' in window) {
      const provider =  await window.solana;
  
      if (provider.isPhantom) {
        return provider;
      }
      else {
        return console.log('Phantom is not available');
      }
    }
  };

  const useWalletFunction = () => {
    setKey(useWallet());
  }

  const provider = getProvider();

  const getBalance = async () => {
    useWalletFunction()
    try {
      if(!provider) return;
      console.log(Wallet.toString());
      setWalletBalance(await connection.getBalance(key.PublicKey));
      console.log(`Balance: ${walletBalance}`);
    }
    catch(err) {
      console.error(err);
    }
  }

  const handleSignAndSendTransaction = async () => {
    if (!provider) return;
    await getBalance();
    if(walletBalance < amount) return console.log('Insufficient funds');
    try{
      const transaction = await createTransferTransaction(provider.publicKey, connection);
      const signature = await provider.signAndSendTransaction(transaction);
      pollSignatureStatus(signature, connection);
    }
    catch(err){
      console.log(err);
    }
  }

  const createTransferTransaction = async (publicKey, connection) => {
    const transaction = new Transaction().add(
      SystemProgram.transfer({
        fromPubkeys : publicKey,
        toPubkeys : publicKey,
        lamports: amount * Math.pow(10, 9),
      })
    )
    return transaction
  }

  const pollSignatureStatus = async(signature, connection) => {

    const interval = setInterval(async () => {
      if (count === MAX_POLLS) {
        clearInterval(interval);
        setSignStatus('failed');
        return;
      }
      
      const { value } = await connection.getSignatureStatus(signature);
    const confirmationStatus = value?.confirmationStatus;

    if (confirmationStatus) {
      const hasReachedSufficientCommitment = confirmationStatus === 'confirmed' || confirmationStatus === 'finalized';

      if (hasReachedSufficientCommitment) {
        clearInterval(interval);
        setSignStatus('success')
        return;
      }
    } else {
      setSignStatus('waiting');
      console.log('Waiting on confirmation...');
    }

    setCount(count+1);

    },POLLING_INTERVAL);
  }

  const connectWallet = async() => {    
    try {
      if(window.phantom?.solana.isPhantom){
        const resp = await window.phantom?.solana.connect();
        setConnected(true)
        return resp.publicKey.toString();
      }
    } 
    catch (err) {
      console.log(err);
      console.log('error at connect wallet');
    }
  };

  const disconectWallet = async() => {
    try{
      await window.solana.disconnect();
      setConnected(false);
    }
    catch(err) {
      console.error(err);
      console.log('Error at wallet disconnect');
    }
    console.log(connected);
  }

  const handleSend = async() => {
    await handleSignAndSendTransaction();
  }

  const handleConnectButton = async() => {
    if(connected) {
      try{
        console.log(`handle Disconnect`);
        disconectWallet();
        setWallet(null);
      }
      catch(e) {
        console.log(e);
        console.log(`error at handle Disonnect`);
      }
      console.log(connected);
    }
    else {
      try{
        console.log(`handle Connect`);
        setWallet(await connectWallet())
      }
      catch(e) {
        console.log(e);
        console.log(`error at handle Connect`);
      }
      console.log(`Connected with Public Key: ${wallet}`)
      console.log(wallet);
    }
  }

  const returnWallet = async() => {
    console.log(wallet);
  }

  return (
    <div className="phantom">

      <h3>Phantom wallet</h3>

      <div>
        <button className="buttons" onClick={handleConnectButton}>{connected ? "Disconnect" : "Connect"}</button>   
        <input className="buttons" onChange={(event) => setAmount(event.target.value)} type="text" value={amount} />
        <button className="buttons" onClick={handleSend} disabled={!connected} >Send</button>   
        <button className="buttons" onClick={returnWallet} >Wallet</button>   
        <button className="buttons" onClick={getBalance} >Balance</button>   
      </div>

      {!connected? <p>Wallet not connected</p> :<p>connected to {wallet.substring(0, 4) + "...." + wallet.substr(wallet.length-4, wallet.length)}</p>}
      {signStatus === "success" ? <p>Transaction Successfull</p> : null }
      {signStatus === "waiting" ? <p>Waiting for conformation...</p> : null }
      {signStatus === "failed" ? <p>Transaction Failed</p> : null }

    </div>
  );
}

export default Phantom;
