const connectWallet = async () => {
  try {
    if (window.phantom?.solana.isPhantom) {
      const resp = await window.solana.connect();
      setConnected(!connected);
      setWallet(resp.publicKey.toString());
      setKey(new PublicKey(resp.publicKey.toString()))
    }
  }
  catch (err) {
    console.log(err);
    console.log('error at connect wallet');
  }
};

const disconectWallet = async () => {
  try {
    await window.solana.disconnect();
    setConnected(!connected);
  }
  catch (err) {
    console.error(err);
    console.log('Error at wallet disconnect');
  }
  console.log(connected);
}

const handleSend = async () => {
  const transaction = new Transaction();
  const signedTransaction = await provider.request({
    method: "signTransaction",
    params: {
      message: "please approve this transaction",
    },
  });

  const signature = await connection.sendRawTransaction(signedTransaction.serialize());
}

const handleConnectButton = async () => {
  if (connected) {
    try {
      console.log(`handle Disconnect`);
      disconectWallet();
      setWallet(null);
    }
    catch (e) {
      console.log(e);
      console.log(`error at handle Disonnect`);
    }
    console.log(connected);
  }
  else {
