import React, { useState, useEffect, useCallback } from "react";
import { Connection, Transaction, SystemProgram, PublicKey, clusterApiUrl, LAMPORTS_PER_SOL } from "@solana/web3.js";
import './phantom.css';

export function Phantom() {

  const [wallet, setWallet] = useState(null);
  const [amount, setAmount] = useState(0);
  const [connected, setConnected] = useState(false);
  const [walletBalance, setWalletBalance] = useState(0);
  const [signStatus, setSignStatus] = useState('');
  const [count, setCount] = useState(0);
  const [key, setKey] = useState(null);
  const [logs, setLogs] = useState([]);

  const MAX_POLLS = 60;
  const POLLING_INTERVAL = 1000;

  const network = clusterApiUrl('devnet');
  const connection = new Connection(network, 'confirmed');

  //------------------------- Logs ------------------------------//

  function addLog(log) {
    setLogs((logs) => [...logs, log]);
  }

  //------------------------- Provider ------------------------------//

  const getProvider = async () => {

    if ('phantom' in window) {
      const provider = await window.phantom?.Connectionsolana;

      if (provider?.isPhantom) {
        return provider;
      }
      else {
        return;
      }
    }
  };

  const provider = getProvider();

  //-------------------------- Connect & Disconnect -----------------------------//

  const connectWallet = async () => {
    try {
      if (window.phantom?.solana.isPhantom) {
        const resp = await window.solana.connect();
        setConnected(!connected);
        setWallet(resp.publicKey.toString());
        setKey(new PublicKey(resp.publicKey.toString()))
      }
    }
    catch (err) {
      console.log(err);
      console.log('error at connect wallet');
    }
  };

  const disconectWallet = async () => {
    try {
      await window.solana.disconnect();
      setConnected(!connected);
    }
    catch (err) {
      console.error(err);
      console.log('Error at wallet disconnect');
    }
    console.log(connected);
  }

  //-------------------------- Transaction 1 -----------------------------//

  function signInTransactionAndSendMoney(destPubkeyStr, lamports) {

    (async () => {

      const transaction = new Transaction();

      const lamports_per_sol = new LAMPORTS_PER_SOL

      lamports = amount * lamports_per_sol;

      try {
        destPubkeyStr = "B1MEySUG5zs3zMC5BEfi8w5p7r6tVDgc93xQynkTw2z3"
        lamports = amount * lamports_per_sol;

        console.log("starting sendMoney");
        const destPubkey = new solanaWeb3.PublicKey(destPubkeyStr);
        const walletAccountInfo = await connection.getAccountInfo(wallet.publicKey);

        console.log("wallet data size", walletAccountInfo?.data.length);
        const receiverAccountInfo = await connection.getAccountInfo(destPubkey);
        console.log("receiver data size", receiverAccountInfo?.data.length);

        const instruction = solanaWeb3.SystemProgram.transfer({
          fromPubkey: wallet.publicKey,
          toPubkey: destPubkey,
          lamports, // about half a SOL
        });

        let trans = await setWalletTransaction(instruction, connection);
        let signature = await signAndSendTransaction(wallet, trans, connection);
        let result = await connection.confirmTransaction(signature, "singleGossip");

        console.log("money sent", result);

      } catch (e) {
        console.warn("Failed", e);
      }
    })
  }

  async function setWalletTransaction(instruction, connection) {

    const transaction = new solanaWeb3.Transaction();

    transaction.add(instruction);
    transaction.feePayer = wallet.publicKey;

    let hash = await connection.getRecentBlockhash();
    console.log("blockhash", hash);

    transaction.recentBlockhash = hash.blockhash;
    return transaction;

  }

  async function signAndSendTransaction(wallet, transaction, connection) {

    const { signature } = await window.solana.signAndSendTransaction(transaction);
    await connection.confirmTransaction(signature);

    console.log("sign transaction");
    console.log("send raw transaction");
    return signature;

  }

  //------------------------- Transaction 0 ------------------------------//


  const handleSend = async () => {

    try {

      const transaction = new Transaction().add(
        SystemProgram.transfer({
          fromPubkey: key,
          toPubkey: key,
          lamports: amount * 10 ^ 9,
        }),
      );

      addLog('Getting recent blockhash');
      transaction.recentBlockhash = await connection.getLatestBlockhash('finalized').blockhash;
      transaction.feePayer = key;

      addLog('Sending signature request to wallet');
      const signedTransaction = await window.solana.signTransaction(transaction);
      addLog('Got signature, submitting transaction');

      const signature = await connection.sendRawTransaction(signedTransaction.serialize());
      addLog('Submitted transaction ' + signature + ', awaiting confirmation');

      await connection.confirmedTransaction(signature, 'singleGossip');
      addLog('Transaction ' + signature + ' confirmed');

    }
    catch (e) {
      console.error(e);
      addLog(`Error: ${e.message}`);
    }
  }

  //------------------------- Connect Button ------------------------------//


  const handleConnectButton = async () => {
    if (connected) {
      try {
        console.log(`handle Disconnect`);
        disconectWallet();
        setWallet(null);
      }
      catch (e) {
        console.log(e);
        console.log(`error at handle Disonnect`);
      }
      console.log(connected);
    }
    else {
      try {
        console.log(`handle Connect`);
        await connectWallet()
      }
      catch (e) {
        console.log(e);
        console.log(`error at handle Connect`);
      }
      console.log(`Connected with Public Key: ${wallet}`)
      console.log(wallet);
    }
  }

  //-------------------------- Wallet Button -----------------------------//

  const returnWallet = async () => {
    console.log(wallet);
  }

  //-------------------------- HTML -----------------------------//


  return (
    <div className="phantom">

      <h3>Phantom wallet</h3>

      <div>
        <button className="buttons" onClick={handleConnectButton}>{connected ? "Disconnect" : "Connect"}</button>
        <input className="buttons" onChange={(event) => setAmount(event.target.value)} type="text" value={amount} />
        <button className="buttons" onClick={handleSend} disabled={!connected} >Send</button>
        <button className="buttons" onClick={returnWallet} disabled={!connected} >Wallet</button>
      </div>

      {wallet === null ? <p>Wallet not connected</p> : <p>connected to {wallet.substring(0, 4) + "...." + wallet.substr(wallet.length - 4, wallet.length)}</p>}
      {signStatus === "success" ? <p>Transaction Successfull</p> : null}
      {signStatus === "waiting" ? <p>Waiting for conformation...</p> : null}
      {signStatus === "failed" ? <p>Transaction Failed</p> : null}

    </div>
  );
}

export default Phantom;
